import React, { useRef } from 'react'; // Import useRef
import Header from './components/Header';
import './App.css';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import homeImage from './images/home.jpg';
import project1Image from './images/project1.jpg';
import project2Image from './images/project2.jpg';
import linkedinIcon from './images/linkedin.png';
import instagramIcon from './images/instagram.png';
import githubIcon from './images/github.png';

const App = () => {
  const projects = [
    { title: 'Sign Bridge', image: project1Image, description: 'Description for Project 1' },
    { title: 'UTimetable', image: project2Image, description: 'https://github.com/CSC207-2022F-UofT/course-project-t_t' },
    // Add more projects here
  ];

  // Create a ref for the social media section
  const socialMediaRef = useRef(null);

  // Function to scroll to the social media section
  const scrollToSocialMedia = () => {
    socialMediaRef.current.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div className="app">
      <Header />
      <main className="main-content">
        {/* ... */}

        <div className="contact">
          <p onClick={scrollToSocialMedia}>Contact me at: narae828@hotmail.com</p>
        </div>

        {/* Social Media Links */}
        <div className="social-media" ref={socialMediaRef}>
          {/* ... */}
        </div>

        <footer className="footer">
          <p>&copy; {new Date().getFullYear()} Narae Lee. All rights reserved.</p>
        </footer>
      </main>
    </div>
  );
};

export default App;
